function rootScene_setActive(scene){
    var ownerScene;
    if(scene && (ownerScene = scene.ownerScene)){
        scene = ownerScene;
    }
    
    if(this._active !== scene){
        if(this._active){
            scene_setActive.call(this._active, false);
        }
        
        this._active = scene || null;
        
        if(this._active){
            scene_setActive.call(this._active, true);
        }
        return true;
    }
    return false;
}

