                            // begin diff
                            var diff = origDomain[0] - currDomain[0];
                            if(diff > 0){
                                roundingPaddings.begin = diff / currLength;
                            }
                            
                            // end diff
                            diff = currDomain[1] - origDomain[1];
                            if(diff > 0){
                                roundingPaddings.end = diff / currLength;
                            }
